export function isUnrecognizedPaymentMethod(entity) {
  return entity.type !== "PaymentMethodCard" && entity.type !== "PaymentMethodConvenienceStore" && entity.type !== "PaymentMethodEasyPay" && entity.type !== "PaymentMethodGiftCertificate" && entity.type !== "PaymentMethodMobile" && entity.type !== "PaymentMethodTransfer" && entity.type !== "PaymentMethodVirtualAccount";
}
